.globl ___start			# leave this here for the moment

#BEGIN{{

# Calculates a prime number close to the input
# INPUT:
# - $a0 = unsigned int n:	Reference n, Number to start looking for a prime
# OUTPUT:
# - $v0 = unsigned int: 	Prime number close to n
getprime:
	move $s0, $a0				## s0 = n = a0;
	li $s1, 1500				## s1 = MAX_NUMBERS;
	bge $s0, $s1, error			## if (n >= MAX_NUMBERS) goto error;
	li $t1, 5					## t1 = 5;
	bge $s0, $t1, sieve			## if (n >= 5) goto sieve;
	li $t1, 2					## t1 = 2;
	bgt $s0, $t1, three			## if (n < 2) {
	move $v0, $t1				## 	return 2;
	jr $ra						## }
	three:						## else if (n > 2 && n < 5) {
	li $t1, 3					## 	t1 = 3;
	move $v0, $t1				## 	return 3;
	jr $ra						## }
	sieve:						## if (n >= 5):
	lw $t2, primes				## 	t2 = *primes;
	beq $t2, $zero, endLoop		## 	if (*primes == 0) goto endLoop; // already generated
	sw $zero, primes			## 	*primes = 0; // mark as already generated
	li $t2, 1					## 	t2 = i = 1;
	loopHead:					## 	for (i = 2; i < MAX_NUMBERS; i++) {
	addiu $t2, 1				## 		i++;
	bge $t2, $s1, endLoop		## 		if (i >= MAX_NUMBERS) goto endLoop;
	la $t0, primes				## 		t0 = &primes;
	addu $t0, $t0, $t2			## 		t0 += i;
	lw $t7, ($t0)				## 		t7 = *t0;
	beq $t7, $zero, loopHead	##  	if (!primes[i])	continue;
	move $t3, $t2				## 		t3 = j = i;
	innerLoop:					##		for (j = i; j * i < MAX_NUMBERS; j++) {
	multu $t3, $t2				## 			hi & lo = i * j;
	mfhi $t4					## 			t4 = hi;
	bne $t4, $zero, loopHead	## 			if (t4 != 0) break; // overflow
	mflo $t4					## 			t4 = lo = i * j;
	bge $t4, $s1, loopHead		##			if (t4 > MAX_NUMBERS) break;
	la $t0, primes				##			t0 = &primes;
	addu $t0, $t0, $t4			## 			t0 += i*j;
	sw $zero, ($t0)				##			primes[i*j] = 0;
	addiu $t3, 1				## 			j++;
	j innerLoop					## 		}
	endLoop:					## 	}
	j findClosest				## 	findClosest();
#END}}
error:
	move $v0, $zero				## return 0;
	jr $ra						## return;

findClosest:
	move $t7, $s0				## t7 = n;
	move $t6, $s0				## t6 = n;
	addiu $t6, 1				## t6++;
	whileLoop:					## while (!primes[t7] && !primes[t6]) {
	addiu $t7, $t7, 1			## 	t7++;
	addiu $t6, $t6, -1			## 	t6--;
	la $t5, primes				## 	t5 = &primes;
	addu $t5, $t5, $t6			## 	t5 += t6;
	lw $t4, ($t5)				## 	t4 = *t5;
	bne $t4, $zero, returnClosest	## 	if (t4 != 0) goto returnClosest;
	la $t5, primes				##	t5 = &primes;
	addu $t5, $t5, $t7			## 	t5 += t7;
	lw $t4, ($t5)				##  t4 = *t5;
	bne $t4, $zero, returnClosest	## if (t4 != 0) goto returnClosest;
	j whileLoop					## }

returnClosest:
	la $t4, primes				## t4 = &primes;
	subu $v0, $t5, $t4			## v0 = &primes[n] - t4;
	jr $ra						## return v0;
	

___start:
main:
	
	li $v0, 5 
	syscall  		## scanf("%d", &a)
	move $a0, $v0

	jal getprime		## sieve

	move $a0, $v0	
	li $v0, 1		## printf("%d", res)
	syscall 

	li $v0, 10		# syscall code 10 for terminating the program
	syscall

