# Calculates a prime number close to the input
# INPUT:
# - $a0 = unsigned int n:	Reference n, Number to start looking for a prime
# OUTPUT:
# - $v0 = unsigned int: 	Prime number close to n
getprime:
	move $s0, $a0				## b0 = n
	
	li $t1, 1500				## t1 = MAX_NUMBERS
	bge $s0, $t1, error			## if n >= 1500: error
	li $t1, 5					
	bge $s0, $t1, sieve			## if (n >= 5) goto sieve;
	li $t1, 2					## t1 = 2;
	bgt $s0, $t1, three			## if (n < 2) {
	move $v0, $t1				## 	return 2;
	jr $ra						## }
	three:						## else if (n > 2 && n < 5) {
	li $t1, 3					## 	t1 = 3;
	move $v0, $t1				## 	return 3;
	jr $ra						## }
	sieve:						## if (n >= 5) {
	li $t2, 1					## t2 = i = 1;
	loopHead:					## for (i = 2; i < MAX_NUMBERS; i++) {
	addiu $t2, 1				## 	i++;
	bge $t2, $t1, endLoop		## 	if (i >= MAX_NUMBERS) goto endLoop;
	la $t0, (primes)			## 	t0 = &primes;
	addu $t0, $t2				## 	t0 += i;
	lb $t7, $t0					## 	t7 = *t0;
	beq $t7, $zero, loopHead	##  if (!primes[i])	continue;
	move $t3, $t2				## 	t3 = j = i;
	innerLoop:					##	for (j = 0; j * i < MAX_NUMBERS; j++) {
	multu $t3, $t2				## 		i * j;
	mfhi $t4					## 		t4 = high;
	bne $t4, $zero, loopHead	## 		if (overflow != null) break;
	mflo $t4					## 		t4 = i * j;
	bge $t4, $t0, loopHead		##		if (j*i > MAX_NUMBERS) break;
	la $t0, (primes)			##		t0 = &primes;
	addu $t0, $t4				## 		t0 += i*j;
	sb $zero, $t0				##		primes[i*j] = 0
	addiu $t3, 1				## 		j++;
	j innerLoop					## 	}
	endLoop:					## }
	
#END}}

